export default function ScrollHooks (){
    const [ScrollY, setScrollY] = useState(0)

    useEffect(() => {
      console.log("Fase de Montaje");
    })
    
    return(
        <>
            <h2>Hooks - useEffect y el ciclo de vida</h2>
        </>
    )
}
