import React, { Component } from "react";

export default class Padre extends Component{
    state = {
        contador:0
    }
    render(){
        return(
            <>
                <h2>Comunicacion Componentes</h2>
                <Hijo mensaje="Para hijo 1"/>
                <Hijo mensaje="Para hijo 2"/>
            </>
        )
    }
}

function Hijo(props) {
    return <h2>{props.mensaje}</h2>
}
